import React, { useEffect,useState } from 'react'
import { View, Text, SafeAreaView, ScrollView } from 'react-native'
import Categories from '../components/Categories'
import HeaderTabs from '../components/HeaderTabs'
import RestautantItems, { localRestaurants } from '../components/RestautantItems'
import SearchBar from '../components/SearchBar'


const YELP_API_KEY =
  "bdRJutLhFAQJ36t7b89CWjHFBU4OKzjt9wvZzcY-nkgmvTqlNMjZWV1eG7iBQ9R74SyfxRg9LWnBAkZY06BtAZAe4d2dfX-2vuX8a1l5V7foctHfX9UKEyoM5ts3YXYx";




    export default function Home() {

    const [restaurantData, setRestaurantData] = useState(localRestaurants);
    const getRestaurantsFromYelp = () => {
        const yelpUrl = `https://api.yelp.com/v3/businesses/search?term=restaurants&location=SanDiego`;

        const apiOptions = {
          headers: {
            Authorization: `Bearer ${YELP_API_KEY}`,
          },
        };

        return fetch(yelpUrl, apiOptions)
        .then((res) =>res.json)
        .then((json) =>setRestaurantData(json.businesses));
    };

useEffect(() => {
    // getRestaurantsFromYelp();
  },[]);
    return (
    <SafeAreaView style={{backgroundColor:"#eee", flex:1}} >
        <View style={{backgroundColor: "white", padding:15 }}  >
            <HeaderTabs />
              <SearchBar/>
        </View>
            <ScrollView showsVerticalScrollIndicator={false}>
                <Categories/>
                <RestautantItems restaurantData={restaurantData} />

            </ScrollView>    
    </SafeAreaView>
    )
}
