import React from 'react'
import { View, Text, Image, TouchableOpacity } from 'react-native'
import MaterialCommunityIcons from "react-native-vector-icons/MaterialCommunityIcons";



export const localRestaurants = [
    {
      name: "Beachside Bar",
      image_url:
        "https://static.vecteezy.com/system/resources/previews/000/087/073/original/restaurant-interior-vector.jpg",
      categories: ["Cafe", "Bar"],
      price: "$$",
      reviews: 1244,
      rating: 4.5,
    },
    {
      name: "Benihana",
      image_url:
        "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8cmVzdGF1cmFudCUyMGludGVyaW9yfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&w=1000&q=80",
      categories: ["Cafe", "Bar"],
      price: "$$",
      reviews: 1244,
      rating: 3.7,
    },
    {
      name: "India's Grill",
      image_url:
        "https://images.unsplash.com/photo-1517248135467-4c7edcad34c4?ixid=MnwxMjA3fDB8MHxzZWFyY2h8MXx8cmVzdGF1cmFudCUyMGludGVyaW9yfGVufDB8fDB8fA%3D%3D&ixlib=rb-1.2.1&w=1000&q=80",
      categories: ["Indian", "Bar"],
      price: "$$",
      reviews: 700,
      rating: 4.9,
    },
  ];










export default function RestautantItems() {
    return (
        <TouchableOpacity activeOpacity={1} style={{marginBottom:30}}>
           {localRestaurants.map((restaurant, index) =>( 
            <View  key={index} style={{marginTop:10, padding:15, backgroundColor:"white"}}>
                <RestaurantImage image ={restaurant.image_url}/>
                <RestaurantInfo name={restaurant.name} rating={restaurant.rating} />
            </View>
           ))}
        </TouchableOpacity>
    )
}

const RestaurantImage =() =>(

    <>
        <Image
        source={{ uri:"https://get.pxhere.com/photo/cafe-light-interior-restaurant-bar-hotel-bar-175068.jpg" }}

        style={{width:"100%", height:180}}
    />
    <TouchableOpacity style={{position:"absolute", right:20, top:20}}>
        <MaterialCommunityIcons name="heart-outline" size={25} color="#fff"/>

    </TouchableOpacity>
    </>
    );
    const RestaurantInfo =() =>(
        <View 
        style={{
            flexDirection:"row",
            justifyContent:"space-between",
            alignItems:"center",
            marginTop:10,
        }}>
            <View>
                <Text style={{fontSize:15, fontWeight:"bold"}}>
                    Farmhouse kiten thai cuisine
                </Text>
                {/* time of delivery */}
                <Text style={{fontSize:13,color:"gray"}} >30-25</Text>
            </View>

            {/* Ratting */}
            <View style={{
                backgroundColor:'#eee',
                height:30,
                width:30,
                alignItems:"center", 
                justifyContent:"center",
                borderRadius:15,}}>
                <Text >4.5</Text>
            </View>
        </View>
    )
