import React from 'react'
import { View, Text, Image } from 'react-native'


const image ="https://cdn.greatlifepublishing.net/wp-content/uploads/sites/2/2020/04/29190236/chilis-3-1588018662.jpg"
const title='Farmhouse kitchen thai Cuisine'
const description = 'Thai • Comfort Food • $$ • 4⭐(2913+)';



export default function About() {
    return (
        <View>
            <RestaurantImage image={image}/>
            <RestaurantTitle title={title}/>
            <RestaurantDescription description={description} />
            </View>
    )
}

const RestaurantImage =(props) =>(
    <Image  source = {{uri: props.image}} style={{window:"100%", height:180,}} />
)

const RestaurantTitle =(props) =>( <Text style={{
    fontSize:29,
    fontWeight:"600",
    marginTop:10,
    marginHorizontal:15,
}}>
{props.title}
</Text>
)
const RestaurantDescription =(props)=>(
    <Text> 
    {props.description}
    </Text>
)
